package java_basis;

public class method {
	public method() { //생성자
		// TODO Auto-generated constructor stub
	}
	public void int_void(int b) {
		//바로 실행되는 메소드 //변수에 담아 사용할 필요가 없다.
		// 매개변수로 자료형이 int b 라는 변수로 활동 한다.
		b=10;

		b += 10;
	}

	public int int_return(int b) {
		//바로 실행되는 메소드 //변수에 담아 사용할 필요가 없다.
		// 매개변수로 자료형이 int b 라는 변수로 활동 한다.
		b=10;

		b += 10;

		return b;
	}



}
